import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import reportWebVitals from './reportWebVitals';

import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import ArrowUpwardIcon from '@mui/icons-material/ArrowUpward';
import ArrowDownwardIcon from '@mui/icons-material/ArrowDownward';
import AddCircleIcon from '@mui/icons-material/AddCircle';

import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';

class FormDialog extends React.Component{
    constructor() {
        super();
        this.state={
            open:false,
            setOpen:false,
        }
    }
     handleClickOpen = () => {
        this.setState({
            setOpen:true
        })
    };
    handleChange(event){
        this.value=event.target.value
    }
     handleClose = () => {
         this.setState({
             setOpen:false
         })
    };

     handleSubmit = (value) => {
         this.setState({
             setOpen:false
         })

    };
     render(){
         return (
             <div>
                 <Button startIcon={<AddCircleIcon/>} onClick={this.handleClickOpen}/>
                 <Dialog open={this.state.open} onClose={this.handleClose}>
                     <DialogTitle>Subscribe</DialogTitle>
                     <DialogContent>
                         <DialogContentText>
                             To subscribe to this website, please enter your email address here. We
                             will send updates occasionally.
                         </DialogContentText>
                         <TextField
                             autoFocus
                             margin="dense"
                             id="name"
                             label="Task Name"
                             type="text"
                             fullWidth
                             variant="standard"
                             value={this.state.value}
                             onChange={this.handleChange}
                         />
                     </DialogContent>
                     <DialogActions>
                         <Button onClick={this.handleClose}>Cancel</Button>
                         <Button onClick={this.handleSubmit()}>Subscribe</Button>
                     </DialogActions>
                 </Dialog>
             </div>
         );
     }

}



class Task extends React.Component{
    constructor(props) {
        super(props)
        this.state={
            name:'TestName',
            description:'sometext',
            deadline:'2000-12-12',
        }
    }
    AddTest(){
        return null
    }
    render(){
        return(
            <Card sx={{maxWidth:300}}>
                <CardContent>
                    <Typography>
                        Név:{this.state.name}
                    </Typography>
                    <Typography>
                        Leírás: {this.state.description}
                    </Typography>
                    <Typography>
                        Deadline:{this.state.deadline}
                    </Typography>
                </CardContent>
                <CardActions>
                    <Button startIcon={<ArrowUpwardIcon/>}/>
                    <Button startIcon={<ArrowDownwardIcon/>} />
                    <FormDialog props={this.props}/>
                </CardActions>
            </Card>


        )
    }
}
