class ProjectBox extends React.Component {
    constructor(props) {
        super(props);
        this.state={
            datalines:Array(10).fill(''),
            test:'testname',
            value:'',
            count:0,
            output:''
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({value: event.target.value});
    }

    handleSubmit(event) {
        this.setState({output: this.state.value })
        event.preventDefault();
        let newarray=this.state.datalines
        let prevcount=this.state.count
        newarray[this.state.count]=this.state.value
        this.setState({
            count:prevcount+1,
            datalines:newarray})
    }
    resetdatalines(){
        this.setState({
            datalines:Array(10).fill(null),
            count:0
        })
    }
    emptydataline(i){
        let datalinesnew=this.state.datalines
        datalinesnew[i]=null
        this.setState({
            datalines:datalinesnew,
        })
        this.reorderdatalines(i)
        this.renderdatalines()
    }
    reorderdatalines(i){
        let datalinesnew=this.state.datalines
        let datacnt=this.state.count
        if(i>=datacnt)
            return
        for(let j=i;j<datacnt;j++){
            datalinesnew[j]=datalinesnew[j+1]
        }
        this.setState({
            datalines: datalinesnew,
            count:datacnt-1
        })
    }
    updataline(i){
        let datalinesnew=this.state.datalines
        let buffer=datalinesnew[i-1]
        if(i===0)
            return
        datalinesnew[i-1]=datalinesnew[i]
        datalinesnew[i]=buffer
        this.setState({
            datalines:datalinesnew
        })
    }
    downdataline(i){
        let datalinesnew=this.state.datalines
        let buffer=datalinesnew[i+1]
        if(i===6 || this.state.count<=i+1 || datalinesnew[i-1]===null)
            return
        datalinesnew[i+1]=datalinesnew[i]
        datalinesnew[i]=buffer
        this.setState({
            datalines:datalinesnew
        })
    }

    renderdatalines(){
        let datalinesrender=[]
        for(let i=0; i<5; i++){
            datalinesrender.push(
                <div className="margin">
                    <Dataline value={this.state.datalines[i]}/>
                    <Clearbutton onClick={()=>this.emptydataline(i)}/>
                    <button className="upbutton" onClick={()=>this.updataline(i)}>UP</button>
                    <button className="downbutton" onClick={()=>this.downdataline(i)}>DOWN</button>
                </div>
            )
        }
        return datalinesrender
    }
    render() {

        return (
            <div>
                <h2>Project Name: <input className="inputfields" type="text"/></h2>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        Task:
                        <input className="inputfields" type="text" value={this.state.value} onChange={this.handleChange} />
                    </label>
                    <input type="submit" className="inputbutton" value="Submit"/>
                </form>
                <button className="clearbutton" onClick={()=>this.resetdatalines()}>
                    Clear All
                </button>
                {this.renderdatalines()}
            </div>
        );
    }
}

