import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import reportWebVitals from './reportWebVitals';

import cx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import Avatar from '@material-ui/core/Avatar';
import Box from '@material-ui/core/Box';
import Card from '@material-ui/core/Card';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import IconButton from '@material-ui/core/IconButton';
import Share from '@material-ui/icons/Share';
import { useSoftRiseShadowStyles } from '@mui-treasury/styles/shadow/softRise';
import { useSlopeCardMediaStyles } from '@mui-treasury/styles/cardMedia/slope';
import { useN01TextInfoContentStyles } from '@mui-treasury/styles/textInfoContent/n01';
import TextInfoContent from '@mui-treasury/components/content/textInfo';

const useStyles = makeStyles(() => ({
    root: {
        maxWidth: 304,
        margin: 'auto',
    },
    content: {
        padding: 24,
    },
    avatar: {
        width: 50,
        height: 50,
        border: '2px solid #fff',
        margin: '-48px 32px 0 auto',
        '& > img': {
            margin: 0,
        },
    },
}));

export const PostCardDemo = React.memo(function PostCard() {
    const cardStyles = useStyles();
    const mediaStyles = useSlopeCardMediaStyles();
    const shadowStyles = useSoftRiseShadowStyles();
    const textCardContentStyles = useN01TextInfoContentStyles();
    return (
        <Card className={cx(cardStyles.root, shadowStyles.root)}>
            <CardMedia
                classes={mediaStyles}
                image={
                    'https://images.unsplash.com/photo-1517147177326-b37599372b73?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2229&q=80'
                }
            />
            <Avatar className={cardStyles.avatar} src={'https://i.pravatar.cc/300'} />
            <CardContent className={cardStyles.content}>
                <TextInfoContent
                    classes={textCardContentStyles}
                    heading={'First Snow Storm'}
                    body={
                        'Snow storm coming in Sommaroy island, Arctic Norway. This is something that you definitely wanna see in your life.'
                    }
                />
            </CardContent>
            <Box px={2} pb={2} mt={-1}>
                <IconButton>
                    <Share />
                </IconButton>
                <IconButton>
                    <FavoriteBorderRounded />
                </IconButton>
            </Box>
        </Card>
    );
});

class Task extends React.Component{
    constructor(props) {
        super(props)
        this.state={
            name:'TestName',
            description:'sometext',
            deadline:'2000-12-12',
        }
    }
    render(){
        return(
            <label className="dataline">
                Név:{this.state.name}   Leírás: {this.state.description}
                Deadline:{this.state.deadline}
            </label>
        )
    }
}
class TaskContainer extends React.Component{
    constructor() {
        super();
        this.state={
            tasks:[],
            taskcnt:0,
        }
    }

    addTask(name,description,date){
        let tasks=this.state.tasks
        tasks.push() //TODO
        this.setstate({
            tasks:tasks,
        })
    }

    render() {
        return (
            <label>
                TESZT
            </label>
        );
    }
}



class Dataline extends React.Component{
    constructor(props) {
        super(props);
    }
    render(i){
        return(
            <button className="dataline">
                {this.props.value}
            </button>
        )
    }
}
class Clearbutton extends React.Component{
    constructor(props) {
        super(props);
    }
    render(i) {
        return(
            <button className="clearbutton" onClick={()=>this.props.onClick()}>Clear</button>
        )
    }
}

class ProjectBox extends React.Component {
    constructor(props) {
        super(props);
        this.state={
            datalines:Array(10).fill(''),
            test:'testname',
            value:'',
            count:0,
            output:''
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({value: event.target.value});
    }

    handleSubmit(event) {
        this.setState({output: this.state.value })
        event.preventDefault();
        let newarray=this.state.datalines
        let prevcount=this.state.count
        newarray[this.state.count]=this.state.value
        this.setState({
            count:prevcount+1,
            datalines:newarray})
    }
    resetdatalines(){
        this.setState({
            datalines:Array(10).fill(null),
            count:0
        })
    }
    emptydataline(i){
        let datalinesnew=this.state.datalines
        datalinesnew[i]=null
        this.setState({
            datalines:datalinesnew,
        })
        this.reorderdatalines(i)
        this.renderdatalines()
    }
    reorderdatalines(i){
        let datalinesnew=this.state.datalines
        let datacnt=this.state.count
        if(i>=datacnt)
            return
        for(let j=i;j<datacnt;j++){
            datalinesnew[j]=datalinesnew[j+1]
        }
        this.setState({
            datalines: datalinesnew,
            count:datacnt-1
        })
    }
    updataline(i){
        let datalinesnew=this.state.datalines
        let buffer=datalinesnew[i-1]
        if(i===0)
            return
        datalinesnew[i-1]=datalinesnew[i]
        datalinesnew[i]=buffer
        this.setState({
            datalines:datalinesnew
        })
    }
    downdataline(i){
        let datalinesnew=this.state.datalines
        let buffer=datalinesnew[i+1]
        if(i===6 || this.state.count<=i+1 || datalinesnew[i-1]===null)
            return
        datalinesnew[i+1]=datalinesnew[i]
        datalinesnew[i]=buffer
        this.setState({
            datalines:datalinesnew
        })
    }

    renderdatalines(){
        let datalinesrender=[]
        for(let i=0; i<5; i++){
            datalinesrender.push(
                <div className="margin">
                    <Dataline value={this.state.datalines[i]}/>
                    <Clearbutton onClick={()=>this.emptydataline(i)}/>
                    <button className="upbutton" onClick={()=>this.updataline(i)}>UP</button>
                    <button className="downbutton" onClick={()=>this.downdataline(i)}>DOWN</button>
                </div>
            )
        }
        return datalinesrender
    }
    render() {

        return (
            <div>
                <h2>Project Name: <input className="inputfields" type="text"/></h2>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        Task:
                        <input className="inputfields" type="text" value={this.state.value} onChange={this.handleChange} />
                    </label>
                    <input type="submit" className="inputbutton" value="Submit"/>
                </form>
                <button className="clearbutton" onClick={()=>this.resetdatalines()}>
                    Clear All
                </button>
                {this.renderdatalines()}
            </div>
        );
    }
}

class Main extends React.Component {
    constructor(props) {
        super(props);
        this.state={
            numberOfprojects:0,
            projectArray:Array(3),
            maxprojectsnumber:3
        }
    }

    addProject(){
        let projects= []
        for(let j=0;j<this.state.numberOfprojects;j++){
            projects.push(this.state.projectArray[j])
        }
        if(this.state.numberOfprojects>=this.state.maxprojectsnumber)
            return
        projects.push(
            <div className="databox">
                {<ProjectBox />}
            </div>)
        this.setState({
            projectArray:projects,
            numberOfprojects:this.state.numberOfprojects+1
        })
    }
    clearProjects(){
        this.setState({
            projectArray:Array(3),
            numberOfprojects:0
        })
    }
    render() {
        const status = 'Current Project: Témalabor';
        return (
            <div className="game">
                <TaskContainer/>
                <PostCardDemo/>
                <Task/>
                <h1>{status}
                    <button className="projectbuttons" onClick={()=>this.addProject()}> Add Project</button>
                    <button className="projectbuttons" onClick={()=>this.clearProjects()}> Clear Projects</button>
                </h1>
                {this.state.projectArray}
            </div>
        );
    }
}

// ========================================

ReactDOM.render(
    <Main />,
    document.getElementById('root')
);

