import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import reportWebVitals from './reportWebVitals';

import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import ArrowUpwardIcon from '@mui/icons-material/ArrowUpward';
import ArrowDownwardIcon from '@mui/icons-material/ArrowDownward';
import AddCircleIcon from '@mui/icons-material/AddCircle';
import DeleteIcon from '@mui/icons-material/Delete';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import Stack from '@mui/material/Stack';
import Paper from '@mui/material/Paper';
import { styled } from '@mui/material/styles';
import {Box, Grid} from "@material-ui/core";
import {Item} from "@mui-treasury/components/flex";

const darkTheme = createTheme({
    palette: {
        mode: 'dark',
    },
});


class ProjectBox extends React.Component {
    constructor(props) {
        super(props);
        this.state={
            datalines:Array().fill(''),
            datas:Array().fill(''),
            opens:Array().fill(false),
            test:'testname',
            value:'',
            value2:'',
            valuemodify:'aaaaa',
            value2modify:'bbbbb',
            count:0,
            output:'',
            open:false,
            openmodify:false,
            modifydx:0,
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleSubmitmodify = this.handleSubmitmodify.bind(this);
        this.handleChange2 = this.handleChange2.bind(this);
        this.handleChange2modify = this.handleChange2modify.bind(this);
        this.handleChangemodify = this.handleChangemodify.bind(this);
    }

    handleClickOpen(){
        this.setState({open:true})
    }
    handleClose(){
        this.setState({open:false})
    }
    handleClickOpenmodify(dx){
        let boolarray=this.state.opens
        boolarray[dx]=true
        let array1=this.state.datalines
        let array2=this.state.datas

        this.setState({opens:boolarray,
        valuemodify:array1[dx],
            value2modify:array2[dx],
            modifydx:dx
        })
    }
    handleClosemodify(dx){
        let boolarray=this.state.opens
        boolarray[dx]=false
        this.setState({opens:boolarray})
    }

    handleChange(event) {
        this.setState({value: event.target.value});
    }
    handleChange2(event) {
        this.setState({value2: event.target.value});
    }

    handleChangemodify(event) {
        this.setState({valuemodify: event.target.value});
    }
    handleChange2modify(event) {
        this.setState({value2modify: event.target.value});
    }

    handleSubmit(event) {
        this.setState({output: this.state.value })
        event.preventDefault();
        let newarray=this.state.datalines
        let newarray2=this.state.datas
        let prevcount=this.state.count
        newarray[this.state.count]=this.state.value
        newarray2[this.state.count]=this.state.value2
        this.setState({
            count:prevcount+1,
            datalines:newarray,
            datas:newarray2,
            value:'',
            value2:''
        })
        this.handleClose()
    }
    handleSubmitmodify(event) {
        this.setState({output: this.state.value })
        event.preventDefault();
        let newarray=this.state.datalines
        let newarray2=this.state.datas
        let dx=this.state.modifydx
        newarray[dx]=this.state.valuemodify
        newarray2[dx]=this.state.value2modify
        this.setState({
            datalines:newarray,
            datas:newarray2,
            valuemodify:'',
        })
        this.handleClosemodify(dx)
    }
    resetdatalines(){
        this.setState({
            datalines:Array(),
            count:0
        })
    }
    emptydataline(i){
        let datalinesnew=this.state.datalines
        datalinesnew[i]=null
        this.setState({
            datalines:datalinesnew,
        })
        this.reorderdatalines(i)
        this.renderdatalines()
    }
    reorderdatalines(i){
        let datalinesnew=this.state.datalines
        let datacnt=this.state.count
        if(i>=datacnt)
            return
        for(let j=i;j<datacnt;j++){
            datalinesnew[j]=datalinesnew[j+1]
        }
        this.setState({
            datalines: datalinesnew,
            count:datacnt-1
        })
    }
    updataline(i){
        let datalinesnew=this.state.datalines
        let buffer=datalinesnew[i-1]
        if(i===0)
            return
        datalinesnew[i-1]=datalinesnew[i]
        datalinesnew[i]=buffer
        this.setState({
            datalines:datalinesnew
        })
    }
    downdataline(i){
        let datalinesnew=this.state.datalines
        let buffer=datalinesnew[i+1]
        if(i===6 || this.state.count<=i+1 || datalinesnew[i-1]===null)
            return
        datalinesnew[i+1]=datalinesnew[i]
        datalinesnew[i]=buffer
        this.setState({
            datalines:datalinesnew
        })
    }



    renderdatalines(){
        let datalinesrender=[]
