import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import reportWebVitals from './reportWebVitals';

import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import ArrowUpwardIcon from '@mui/icons-material/ArrowUpward';
import ArrowDownwardIcon from '@mui/icons-material/ArrowDownward';


export default function BasicCard() {
    return (
        <Card sx={{ maxWidth: 200 }}>
            <CardContent>
                <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
                    TaskName
                </Typography>
                <Typography variant="body2">
                    Task description
                </Typography>
            </CardContent>
            <CardActions>
                <Button startIcon={<ArrowUpwardIcon/>} >Learn More</Button>
            </CardActions>
        </Card>
    );
}

class Task extends React.Component{
    constructor(props) {
        super(props)
        this.state={
            name:'TestName',
            description:'sometext',
            deadline:'2000-12-12',
        }
    }
    render(){
        return(
            <Card sx={{maxWidth:200}}>
                <CardContent>
                    <Typography>
                        Név:{this.state.name}
                    </Typography>
                    <Typography>
                        Leírás: {this.state.description}
                    </Typography>
                    <Typography>
                        Deadline:{this.state.deadline}
                    </Typography>

                </CardContent>
                <CardActions>
                    <Button startIcon={<ArrowUpwardIcon/>} >Learn More</Button>
                </CardActions>
            </Card>
        )
    }
}
class TaskContainer extends React.Component{
    constructor() {
        super();
        this.state={
            tasks:[],
            taskcnt:0,
        }
    }

    addTask(name,description,date){
        let tasks=this.state.tasks
        tasks.push() //TODO
        this.setstate({
            tasks:tasks,
        })
    }

    render() {
        return (
            <label>
                TESZT
            </label>
        );
    }
}



class Dataline extends React.Component{
    constructor(props) {
        super(props);
    }
    render(i){
        return(
            <button className="dataline">
                {this.props.value}
            </button>
        )
    }
}
class Clearbutton extends React.Component{
    constructor(props) {
        super(props);
    }
    render(i) {
        return(
            <button className="clearbutton" onClick={()=>this.props.onClick()}>Clear</button>
        )
    }
}

class ProjectBox extends React.Component {
    constructor(props) {
        super(props);
        this.state={
            datalines:Array(10).fill(''),
            test:'testname',
            value:'',
            count:0,
            output:''
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({value: event.target.value});
    }

    handleSubmit(event) {
        this.setState({output: this.state.value })
        event.preventDefault();
        let newarray=this.state.datalines
        let prevcount=this.state.count
        newarray[this.state.count]=this.state.value
        this.setState({
            count:prevcount+1,
            datalines:newarray})
    }
    resetdatalines(){
        this.setState({
            datalines:Array(10).fill(null),
            count:0
        })
    }
    emptydataline(i){
        let datalinesnew=this.state.datalines
        datalinesnew[i]=null
        this.setState({
            datalines:datalinesnew,
        })
        this.reorderdatalines(i)
        this.renderdatalines()
    }
    reorderdatalines(i){
        let datalinesnew=this.state.datalines
        let datacnt=this.state.count
        if(i>=datacnt)
            return
        for(let j=i;j<datacnt;j++){
            datalinesnew[j]=datalinesnew[j+1]
        }
        this.setState({
            datalines: datalinesnew,
            count:datacnt-1
        })
    }
    updataline(i){
        let datalinesnew=this.state.datalines
        let buffer=datalinesnew[i-1]
        if(i===0)
            return
        datalinesnew[i-1]=datalinesnew[i]
        datalinesnew[i]=buffer
        this.setState({
            datalines:datalinesnew
        })
    }
    downdataline(i){
        let datalinesnew=this.state.datalines
        let buffer=datalinesnew[i+1]
        if(i===6 || this.state.count<=i+1 || datalinesnew[i-1]===null)
            return
        datalinesnew[i+1]=datalinesnew[i]
        datalinesnew[i]=buffer
        this.setState({
            datalines:datalinesnew
        })
    }

    renderdatalines(){
        let datalinesrender=[]
        for(let i=0; i<5; i++){
            datalinesrender.push(
                <div className="margin">
                    <Dataline value={this.state.datalines[i]}/>
                    <Clearbutton onClick={()=>this.emptydataline(i)}/>
                    <button className="upbutton" onClick={()=>this.updataline(i)}>UP</button>
                    <button className="downbutton" onClick={()=>this.downdataline(i)}>DOWN</button>
                </div>
            )
        }
        return datalinesrender
    }
    render() {

        return (
            <div>
                <h2>Project Name: <input className="inputfields" type="text"/></h2>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        Task:
                        <input className="inputfields" type="text" value={this.state.value} onChange={this.handleChange} />
                    </label>
                    <input type="submit" className="inputbutton" value="Submit"/>
                </form>
                <button className="clearbutton" onClick={()=>this.resetdatalines()}>
                    Clear All
                </button>
                {this.renderdatalines()}
            </div>
        );
    }
}

class Main extends React.Component {
    constructor(props) {
        super(props);
        this.state={
            numberOfprojects:0,
            projectArray:Array(3),
            maxprojectsnumber:3
        }
    }

    addProject(){
        let projects= []
        for(let j=0;j<this.state.numberOfprojects;j++){
            projects.push(this.state.projectArray[j])
        }
        if(this.state.numberOfprojects>=this.state.maxprojectsnumber)
            return
        projects.push(
            <div className="databox">
                {<ProjectBox />}
            </div>)
        this.setState({
            projectArray:projects,
            numberOfprojects:this.state.numberOfprojects+1
        })
    }
    clearProjects(){
        this.setState({
            projectArray:Array(3),
            numberOfprojects:0
        })
    }
    render() {
        const status = 'Current Project: Témalabor';
        return (
            <div className="game">
                <TaskContainer/>
                <BasicCard />
                <Task/>
                <h1>{status}
                    <button className="projectbuttons" onClick={()=>this.addProject()}> Add Project</button>
                    <button className="projectbuttons" onClick={()=>this.clearProjects()}> Clear Projects</button>
                </h1>
                {this.state.projectArray}
            </div>
        );
    }
}

